package week1.day2;

public class PrimeNumber 
{

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		int n= 29;
		if(n%2==0)
		{
			System.out.println("Prime Number" +n); 
		}
		else if (n%3 == 0)
		{System.out.println("Prime Number" +n); }
		else System.out.println("Non Prime Number");
	}

}
